static void ecdsa_rsa_class_init(ObjectClass *klass, void *data)
{
    DeviceClass *dc = DEVICE_CLASS(klass);

    dc->reset = ecdsa_rsa_reset;
    dc->vmsd = &vmstate_ecdsa_rsa;
}

